import numpy as np
from itertools import permutations

NO_FIGHT = 0
FIGHT = 1
numPatrons = 3

class Learner:
    def __init__(self, numPatrons):
        self.PastExp = set()
        self.permutations = list(permutations(range(numPatrons), 2))
        self.HypothesisSpace = [Hypothesis(x, y) for (x,y) in self.permutations]
    
    def predict(self, obs):
        res = set()
        for hypothesis in self.HypothesisSpace:
            res.add(hypothesis.predict(obs))
        
        if len(res) == 1:
            return res.pop()
        else:
            return -1
    
    def updateHSpace(self, obs, trueValue):
        self.HypothesisSpace = [hypothesis for hypothesis in self.HypothesisSpace if hypothesis.predict(obs) == trueValue]
        print("Cardinality", len(self.HypothesisSpace))
        
    
class Hypothesis:
    def __init__(self, instigator_, pacifier_):
        self.instigator = instigator_
        self.pacifier = pacifier_
    
    def predict(self, obs):
        res = NO_FIGHT
        
        if obs[self.instigator] == 1 and obs[self.pacifier] == 0:
            res = FIGHT
        return res
    
    def toString(self):
        print("Instigator", self.instigator, ". Pacifier: ", self.pacifier)

def GenerateTest(n=10, numPatrons=3, instigator = 0, pacifier = 1):
    res = []
    for i in range(n):
        tmp = list(np.random.binomial(1, 0.5, size=numPatrons))
        
        fight_bool = NO_FIGHT
        if tmp[instigator] == 1 and tmp[pacifier] == 0:
            fight_bool = FIGHT
            
        res.append([tmp, fight_bool])
    return res

def NamTest():
    res = GenerateTest(n=20, numPatrons = 4
                       , instigator=1, pacifier=2)
    myLearner = Learner(numPatrons)
    
    for obs, trueValue in res:
        predict = myLearner.predict(obs)
        print(predict)
        if predict == -1:
            myLearner.updateHSpace(obs, trueValue)
    
    myLearner.HypothesisSpace[0].toString()

def Reformat(string):
    tmp =  string.replace("{","[")
    tmp = tmp.replace("}","]")
    return eval(tmp)

